import logo from './logo.svg';
import './App.css';
import {createRequestOptions} from "./requestBuilder";

function App() {
    const generateTodayDate = () => {
        const year = new Date().getFullYear().toString()
        const month = (new Date().getMonth() + 1).toString()
        const day = new Date().getDate().toString()
        return day + "/" + month + "/" + year
    }
    const generateNextInvoiceNumber = (lastInvoiceNumber) => {
        const year = lastInvoiceNumber.substring(0, 4)
        const month = lastInvoiceNumber.substring(4, 6)
        let index = parseInt(lastInvoiceNumber.substring(6))

        const currYear = new Date().getFullYear().toString()
        let currMonth = (new Date().getMonth() + 1).toString()
        if (currMonth.length === 1) {
            currMonth = "0" + currMonth
        }

        if (year === currYear && month === currMonth) {
            index += 1
            index = index.toString()
            while (index.length < 3) {
                index = "0" + index
            }
            return year + month + index
        }
        return currYear + currMonth + "001"
    }

    const type = async () => {
        const getOrdersInfoRO = createRequestOptions('GET')
        const ordersInfoPromise = await fetch('http://localhost:8080/api/orders', getOrdersInfoRO)
        const ordersInfoResult = await ordersInfoPromise.json()
        const ordersSize = ordersInfoResult.data.size
        const lastInvoiceNumber = ordersInfoResult.data.lastInvoiceNumber

        const nextOrderIndex = ordersSize + 1
        const nextOrderRow = nextOrderIndex + 1

        const currInvoiceNumber = generateNextInvoiceNumber(lastInvoiceNumber)
        const today = generateTodayDate()

        const order = {
            row_number: nextOrderRow,
            customer: 'test_name',
            invoice_number: currInvoiceNumber,
            invoice_date: today,
            category: 'test_cat',
            brand: 'test_brand',
            detailed: 'test_detailed',
            color: 'test_color',
            desc: 'test_desc',
            qty: '1',
            discount: '$0.01',
            status: 'PAID',
            remarks: 'test_remarks',
            tips: '$10',
        }
        const createOrderRO = createRequestOptions('POST', order)
        await fetch('http://localhost:8080/api/order', createOrderRO)
    }
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" onClick={() => type()}/>
        <span onClick={() => type()}>Hello</span>
      </header>
    </div>
  );
}

export default App;
